import React from "react";
import { secondsToDuration } from "../utils/duration";
import { minutesToDuration } from "../utils/duration";


function NextSession ({session, focusDuration, breakDuration}) {

    /**
     * State function to transition the current session type to the next session. 
     * e.g. On Break -> Focusing or Focusing -> On Break
     */
    // const focusOrBreak = currentSession => {
    //   if (currentSession.label === "Focusing") {
    //     return {
    //       label: "On Break",
    //       timeRemaining: breakDuration * 60,
    //     };
    //   }
    //   return {
    //     label: "Focusing",
    //     timeRemaining: focusDuration * 60,
    //   };
    // }

  function focusOrBreak(label) {
       if (label === "Focusing"){
           return focusDuration;
       } else {
           return breakDuration;
       } 
    }

    function elapsedTime(totalTime, timeRemaining){
        const seconds = totalTime * 60;
        const elapsedTime = seconds - timeRemaining;
        return (elapsedTime * 100) / seconds
    }

    if (session === null) {
        return null
    } else {
        return (
            <div>
                {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
                <div className="row mb-2">
                <div className="col">
                    {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
                    <h2 data-testid="session-title">
                    {label} for {minutesToDuration(focusOrBreak(label))} minutes
                    </h2>
                    {/* TODO: Update message below correctly format the time remaining in the current session */}
                    <p className="lead" data-testid="session-sub-title">
                    {secondsToDuration(timeRemaining)} remaining
                    </p>
                </div>
                </div>
                <div className="row mb-2">
                <div className="col">
                    <div className="progress" style={{ height: "20px" }}>
                    <div
                        className="progress-bar"
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        // TODO: Increase aria-valuenow as elapsed time increases
                        aria-valuenow={elapsedTime(focusOrBreak(label), timeRemaining)} 
                        // TODO: Increase width % as elapsed time increases
                        style={{ width: elapsedTime(focusOrBreak(label), timeRemaining) + "%" }} 
                    />
                    </div>
                </div>
                </div>
            </div>
            );
    }
}

export default NextSession;
